import { Component, useState, useEffect } from "react"
import axios from 'axios';

import "./style.css"
import { credentials } from "../../credentials"


const DataHound = () => {

    const [token, setToken] = useState('')
    const [data, setData] = useState('')

    useEffect(() => {
        //Get Token
        axios('https://accounts.spotify.com/api/token', {
            headers: {
                'Content-Type': 'application/x-www-form-urlencoded',
                'Authorization': 'Basic ' + btoa(credentials.spotify_API_ID + ':' + credentials.spotify_API_key)
            },
            data: 'grant_type=client_credentials',
            method: 'POST'
        })
            .then(tokenResponse => {
                console.log("Token:", tokenResponse.data.access_token)
                setToken(tokenResponse.data.access_token);

                //Call Spotify API functions
                axios("https://api.spotify.com/v1/search?query=track:Easy%20On%20Me+artist:Adele&type=track&offset=0&limit=20", {
                    method: 'GET',
                    headers: {
                        'Authorization': 'Bearer ' + tokenResponse.data.access_token,
                        'Accept': 'application/json',
                        'Content-Type': 'application/json',
                    }
                })
                    .then(serchResponse => {
                        console.log("Did we get data back?")
                        console.log("Artist",serchResponse.data["tracks"]["items"][0]["artist"]["name"])
                        console.log("Track",serchResponse.data["tracks"]["items"][0]["name"])
                        console.log("Track ID",serchResponse.data["tracks"]["items"][0]["id"])

                    })

            });
    }, []);

    return (
        <div>
            {console.log("Datahound Start")}
            <p>Datahound</p>

        </div>
    )
}

export default DataHound
