import React, { useEffect, useRef } from 'react'


function App() {

    const paypal = useRef()

    useEffect(() => {
        window.paypal
          .Buttons({
            createOrder: (data, actions, err) => {
              return actions.order.create({
                intent: "CAPTURE",
                purchase_units: [
                  {
                    description: "Donation",
                    amount: {
                      currency_code: "USD",
                      value: 1.00,
                    },
                  },
                ],
              });
            },
            onApprove: async (data, actions) => {
              const order = await actions.order.capture();
              console.log(order);
            },
            onError: (err) => {
              console.log(err);
            },
          })
          .render(paypal.current);
      }, []);

    return (
        <div>
            <div ref={paypal}></div>
        </div>
    )
}

export default App
