import { React } from 'react'
import { useHistory } from "react-router-dom";
import { Button } from 'react-bootstrap';

import "./style.css"
import { credentials } from "../credentials"

function App(props) {
    //https://www.youtube.com/watch?v=EwdsnGfrd-k&list=PL7RVBKKO9UqyEr8kqLe9152SIktQjDl64
    const history = useHistory();
    const CLIENT_ID = credentials.spotify_API_ID;
    const SPOTIFY_AUTHORIZE_ENDPOINT = 'http://accounts.spotify.com/authorize';
    const REDIRECT_URL_AFTER_LOGIN = 'http://localhost:3000/';
    const SCOPES = [
        'playlist-modify-private',
        'playlist-modify-public',
        'user-library-modify',
        'user-library-read',
        'user-read-email'
    ]
    const SCOPES_URL_PARAM = SCOPES.join("%20")

    const submitHandler = (event) => {
        event.preventDefault();

        // make the post request here but don't clear the state information.
        // since you'll need the state information to pass between components,
        // once you are done with the confirm page, you can clear the state.
    };

    const handleLogin = () => {
        window.location = '${SPOTIFY_AUTHORIZE_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URL_AFTER_LOGIN}&scope=${SCOPES_URL_PARAM}&response_type=token&show_dialog=true'
    };

    return (
        <div class="jumbotron text-center">
            <h1>Convert Youtube to Spotify!</h1>
            <form onSubmit={submitHandler}>
                <div>
                    <label htmlFor="yturl"><b>Youtube URL</b></label>
                    <br />
                    <p>https://www.youtube.com/watch?v=EwdsnGfrd-k&list=PL7RVBKKO9UqyEr8kqLe9152SIktQjDl64</p>
                    <p>https://www.youtube.com/playlist?list=PLDIoUOhQQPlXr63I_vwF9GD8sAKh77dWU</p>
                    <input onChange={props.onFirstChange} type="text" id="yturl" />
                </div>
                <button type="button" onClick={() => history.push("/convert")}> Convert </button>
            </form>

            <Button variant="info" type="submit" onClick={handleLogin}>
                Login to spotify
            </Button>


            <br />
            <br />
            <table>
                <tr>
                    <th>Plan Price</th>
                    <th>Includes</th>
                </tr>
                <tr>
                    <td>Free</td>
                    <td>Up to 25 songs to convert per submission</td>
                </tr>
                <tr>
                    <td>$5.99/month</td>
                    <td>Unlimited songs to convert per submission</td>
                </tr>
            </table>

        </div>
    )
}

export default App
