import { React, useState } from 'react'
import { useHistory } from "react-router-dom";
import "./style.css"

function App(props) {
    //https://www.youtube.com/watch?v=EwdsnGfrd-k&list=PL7RVBKKO9UqyEr8kqLe9152SIktQjDl64
    var start = 1,
        index = 0,
        matches = [],
        success = document.getElementById('success'),
        failed = document.getElementById('failed'),
        content = document.getElementById('content');

    function load(url, callback) {
        var xhr = new XMLHttpRequest();
        xhr.open('GET', url, true);
        xhr.onreadystatechange = function () {
            if (xhr.readyState === 4) {
                if (xhr.status === 200) {
                    callback(JSON.parse(xhr.responseText));
                } else {
                    callback([{}]);
                }
            }
        };
        xhr.send();
    }

    function loadPage(id) {
        index = 0;
        matches = [];
        if (!id) {
            id = document.getElementById('id').value;
        }
        load('https://gdata.youtube.com/feeds/api/playlists/' + id + '?alt=jsonc&v=2&start-index=' + start + '&max-results=50', function (e) {
            console.log('loadPage', e);
            if (e.data) {
                searchSpotify(e.data.items);
            }
        });
    }

    function searchSpotify(items) {
        var name = items[index].video.title || [];
        name = name.match(/[\w']+/g)
            .join(' ')
            .replace(/official/ig, '')
            .replace(/featuring/ig, '')
            .replace(/feat/ig, '')
            .replace(/video/ig, '')
            .replace(/1080p/ig, '')
            .replace(/music/ig, '');

        load('http://ws.spotify.com/search/1/track.json?q=' + encodeURIComponent(name), function (e) {
            if (e.tracks && e.tracks[0]) {
                console.log('success', name, e);
                // success
                matches.push(e.tracks[0]);
                success.innerHTML += name + '<br/>';
                content.innerHTML += e.tracks[0].href + '<br/>';
            } else {
                console.log('failt', name, e);
                // fail
                failed.innerHTML += name + '<br/>';
            }

            if (index < items.length - 1) {
                index += 1;
                searchSpotify(items);
            } else {
                console.log(matches);
                start += 50;
                index = 0;
                loadPage();
            }
        });
    }

    const history = useHistory();

    const submitHandler = (event) => {
        event.preventDefault();

        // make the post request here but don't clear the state information.
        // since you'll need the state information to pass between components,
        // once you are done with the confirm page, you can clear the state.
    };

    return (
        <div class="jumbotron text-center">
            <h1>Convert Youtube to Spotify!</h1>
            <form onSubmit={submitHandler}>
                <label for="yturl"><b>Youtube URL</b></label>
                <br />
                <input onChange={props.onFirstChange} type="text" id="yturl" value="https://www.youtube.com/watch?v=EwdsnGfrd-k&list=PL7RVBKKO9UqyEr8kqLe9152SIktQjDl64" />
                <br />
                <button type="button" onClick={() => history.push("/convert")}>
                    Confirm
                </button>
            </form>


            <br />
            <br />
            <table>
                <tr>
                    <th>Plan Price</th>
                    <th>Includes</th>
                </tr>
                <tr>
                    <td>Free</td>
                    <td>Up to 25 songs to convert per submission</td>
                </tr>
                <tr>
                    <td>$5.99/month</td>
                    <td>Unlimited songs to convert per submission</td>
                </tr>
            </table>

        </div>
    )
}

export default App
