import { React, useEffect, useState } from 'react'
import { Link } from "react-router-dom";
import axios from 'axios';

import "../style.css"
import { credentials } from "../../credentials"
import DataHound from "./DataHound"


function App(props) {
    const [data, setData] = useState([]);
    const [isLoading, setLoading] = useState(true);
    const [Track_URI, setTrackURI] = useState('');
    const playlist_uris = []

    function get_playlist_id(URL) {

        //console.log("URL in convert", props.userDetails.fullURL)
        //console.log("user_id in convert", localStorage.getItem("user_id"))
        //console.log("user_token in convert", localStorage.getItem("user_token"))
        //https://www.youtube.com/watch?v=EwdsnGfrd-k&list=PL7RVBKKO9UqyEr8kqLe9152SIktQjDl64

        const num_equal_signs = (URL.match(/=/g) || []).length
        if (URL !== null && num_equal_signs === 1)
            return URL.split("=")[1];
        else if (URL !== null && num_equal_signs === 2)
            return URL.split("=")[2];
        else
            return -1
    }

    function get_api_URL(playlist_ID, key) {
        //AIzaSyBK9I7LUg_7JHmSg8QrNhxkwK98MnuOKKQ
        if (playlist_ID === -1)
            return "No GET address"
        else
            return 'https://youtube.googleapis.com/youtube/v3/playlistItems?part=snippet%2CcontentDetails&maxResults=25&playlistId=' + playlist_ID + '&key=' + key
    }

    function get_title_list(get_url) {
        var data = null
        var title_list = []
        fetch(get_url, {
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            }
        })
            .then((res) => res.json())
            .then((json) => {
                data = json
                //trim data
                for (var i = 0; i < data.items.length; i++) {
                    title_list.push(data.items[i].snippet.title)
                }

                //Remove extra
                for (var i = 0; i < title_list.length; i++) {
                    title_list[i] = title_list[i]
                        .replace(/\(.+\)/ig, '')
                        .replace(/\[.+\]/ig, '')
                        .replace(/official/ig, '')
                        .replace(/featuring/ig, '')
                        .replace(/feat/ig, '')
                        .replace(/video/ig, '')
                        .replace(/1080p/ig, '')
                        .replace(/music/ig, '')
                        .replace(/Audio/ig, '')
                }
                //"https://api.spotify.com/v1/search?query=track%3A{}+artist%3A{}&type=track&offset=0&limit=20"
                var query = ""
                var pre_query = "https://api.spotify.com/v1/search?query="
                var post_query = "&type=track&offset=0&limit=20"
                for (var i = 0; i < title_list.length; i++) {
                    if (title_list[i].includes("-")) {
                        query = "track:" + title_list[i].split("-")[1].trim() + "+" +
                            "artist:" + title_list[i].split("-")[0].trim()
                        query = query.replace(/\s/ig, '%20')
                    }
                    else {
                        query = "track:Never%20Gonna%20Give%20You%20Up+artist:Rick%20Ashley"
                    }
                    title_list[i] = pre_query + query + post_query
                }
                //clear data
                props.userDetails.data = title_list
                props.userDetails.DataisLoaded = true
            })
            .then(() => {
                get_tracks(title_list)
            })
    }

    function get_tracks(title_uri_list) {
        var mylist = []
        var spotifyURIs = ""
        axios('https://accounts.spotify.com/api/token', {
            headers: {
                'Content-Type': 'application/x-www-form-urlencoded',
                'Authorization': 'Basic ' + btoa(credentials.spotify_API_ID + ':' + credentials.spotify_API_key)
            },
            data: 'grant_type=client_credentials',
            method: 'POST'
        }).then(tokenResponse => {
            //Loop through and get track ID's

            let URI = ""
            for (var i = 0; i < title_uri_list.length; i++) {
                axios(title_uri_list[i], {
                    method: 'GET',
                    headers: {
                        'Authorization': 'Bearer ' + tokenResponse.data.access_token,
                        'Accept': 'application/json',
                        'Content-Type': 'application/json',
                    }
                }).then(serchResponse => {
                    try {
                        URI = "spotify:track:" + serchResponse.data["tracks"]["items"][0]["id"] + ","//Assign trackID
                        spotifyURIs += URI
                    } catch (error) {
                        URI = "spotify:track:4cOdK2wGLETKBW3PvgPWqT,"//Rick Ashley
                        spotifyURIs += URI
                    }
                    //console.log(URI)
                    console.log("Loop at,", i)
                    if(i == title_uri_list.length){
                        setLoading(false);
                        console.log("spotifyURIs!!!!! Freee??", spotifyURIs)
                        //setTrackURI
                    }

                })//end then(searchResponse)
            } //end For loop
        }).then(() => {


        })
    }



    const { fullURL } = props.userDetails;

    //const spotifyID = 1254511451;
    const playlist_ID = get_playlist_id(fullURL)
    const get_address = get_api_URL(playlist_ID, credentials.youtube_API_key)
    get_title_list(get_address)
    get_tracks(props.userDetails.data)

    if(isLoading){
        return <div className="App">Loading...</div>;
    }

    return (
        <div class="jumbotron text-center">

            <h1>Conversion Page</h1>
            {fullURL ? (
                <h3>{`Converting from: ${fullURL}`}</h3>
            ) : (
                <p>No URL entered</p>
            )}
            <button type="button" onClick={() => props.onClear()}>
                clear
            </button>
            <br />
            <button>
                <Link to="/">Go back </Link>
            </button>



        </div>
    )
}
//<br />
//<br />
//<p><b>playlist_ID:</b> {`${playlist_ID}`}</p>
//<p><b>GET address:</b> {`${get_address}`}</p>
//<p><b>Data:</b> {props.userDetails.data}</p>
export default App
