import { React, useState } from 'react'
import { BrowserRouter, Route, Switch } from 'react-router-dom'

import Login from './pages/Login'
import Register from './pages/Register'
import Home from './pages/Home'
import Navbar from './pages/Navbar/navbar'
import About from './pages/About'
import Convert from './pages/Convert/Convert'
import NotFound from './pages/NotFound'

import "./style.css"

const App = () => {
	const initialState = { fullURL: "", data: [], DataisLoaded: false };
	const [state, setState] = useState(initialState);

	const handleFirstChange = (e) => {
		const newState = { ...state };
		newState.fullURL = e.target.value;
		setState(newState);
	};

	const resetForm = () => {
		setState(initialState);
	};

	return (
		<div className="App">
			<BrowserRouter>
				<Navbar />
				<Switch>
					<Route path="/" exact component={Home} >
						<Home onFirstChange={handleFirstChange} />
					</Route>
					<Route path="/login" exact component={Login} />
					<Route path="/register" exact component={Register} />
					<Route path="/about" exact component={About} />
					<Route path="/convert" exact component={Convert} >
						<Convert userDetails={state} onClear={resetForm} />
					</Route>
					<Route path="*" exact component={NotFound} />
				</Switch>
			</BrowserRouter>
		</div>
	)
}

export default App
