    return (
        <div class="jumbotron text-center">
            <h1>About Page</h1>
            <form onSubmit={setDonation(e.target.value)}>
                <label htmlFor="Don"><b>Donation</b></label>
                <input type="number" min="0.01" step="0.01" id="Don">1</input>
            </form>


            {checkout ? (
                <Paypal />
            ) : (
                <button onClick={() => { setCheckout(true) }}>Donate</button>
            )}

        </div>
    )
}

